// Custom Cantarell Bold font
Graphics.prototype.setFontCantarellBold = function () {
    return this.setFontCustom(
        atob(''),
        46,
        atob("CxIhHyAfISAfICAgCw=="),
        48 | 131072
    );
};

// Wallpapers
var lightWallpaper = require("heatshrink").decompress(atob(""))
var darkWallpaper = require("heatshrink").decompress(atob(""))


// === Helper: Draw thick outline text ===
function drawTextWithOutline(text, x, y, textColor, outlineColor) {
    g.setColor(outlineColor);
    [-3, 0, 3].forEach(dx => {
        [-3, 0, 3].forEach(dy => {
            if (dx || dy) g.drawString(text, x + dx, y + dy);
        });
    });
    g.setColor(textColor);
    g.drawString(text, x, y);
}

// === Helper: Draw thin outline text (for date) ===
function drawTextWithThinOutline(text, x, y, textColor, outlineColor) {
    g.setColor(outlineColor);
    [-2, 0, 2].forEach(dx => {
        [-2, 0, 2].forEach(dy => {
            if (dx || dy) g.drawString(text, x + dx, y + dy);
        });
    });
    g.setColor(textColor);
    g.drawString(text, x, y);
}

// === Main watchface drawing ===
function drawWatchFace() {
    let date = new Date();
    let hours = date.getHours();
    let minutes = date.getMinutes();

    // Determine theme and colors
    let isDarkMode = g.theme.bg < 0x7FFF;
    let textColor = isDarkMode ? 0xFFFF : 0x0000;
    let outlineColor = isDarkMode ? 0x0000 : 0xFFFFFF;

    // Screen size
    let screenWidth = g.getWidth();
    let screenHeight = g.getHeight();
    let centerX = screenWidth / 2;

    // Clear background and draw wallpaper
    g.clear();
    g.drawImage(isDarkMode ? darkWallpaper : lightWallpaper, 0, 0);

    // Format time and date
    let timeStr = (" " + hours).substr(-2) + ":" + ("0" + minutes).substr(-2);
    let months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
        "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
    let dateStr = `${months[date.getMonth()]} ${date.getDate()}, ${date.getFullYear()}`;

    // Estimate sizes
    g.setFontCantarellBold();
    let timeWidth = g.stringWidth(timeStr);
    let timeHeight = 48;
    let spacing = 10;
    let dateHeight = 16;

    let totalHeight = timeHeight + spacing + dateHeight;
    let startY = Math.floor((screenHeight - totalHeight) / 2);

    // === Draw Time ===
    drawTextWithOutline(
        timeStr,
        centerX - (timeWidth / 2),
        startY,
        textColor,
        outlineColor
    );

    // === Draw Date ===
    g.setFont("6x8", 2);
    let dateWidth = g.stringWidth(dateStr);
    drawTextWithThinOutline(
        dateStr,
        centerX - (dateWidth / 2),
        startY + timeHeight + spacing,
        isDarkMode ? 0xAAAA : 0x0000,
        outlineColor
    );
}

// === Auto redraw on screen wake and every minute ===
Bangle.on('lcdPower', function (on) {
    if (on) drawWatchFace();
});
setInterval(drawWatchFace, 60000);
drawWatchFace();

// === Prevent widgets from drawing over the watchface ===
Bangle.on('lcdPower', on => {
    if (on) {
        drawWatchFace();
    } else {
        if (drawTimeout) clearTimeout(drawTimeout);
        drawTimeout = undefined;
    }
});

// Mark this app as a clock so launcher button works
Bangle.setUI({
    mode: 'clock',
    remove: function () {
        if (drawTimeout) clearTimeout(drawTimeout);
        drawTimeout = undefined;
        delete Array.prototype.sample;
        require('widget_utils').show();
    }
});

Bangle.loadWidgets();
require("widget_utils").swipeOn(); // hides widgets but lets you swipe to see them


